    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double uts = Double.parseDouble(jTextField2.getText());
        double tm = Double.parseDouble(jTextField3.getText());
        double uas = Double.parseDouble(jTextField4.getText());
        double ave = (uts+tm+uas)/3;
        jLabel9.setText("Nilai Rata-Rata : "+ave);
        jLabel8.setText("Nama : " +jTextField1.getText());
        if(ave>=90){
            jLabel10.setText("Grade : A");
            jLabel11.setText("Hasil : Lulus");
        }else if(ave>=80 && ave<=89){
        jLabel10.setText("Grade : B");
        jLabel11.setText("Hasil : Lulus");
    }else if(ave>=70 && ave<=79){
        jLabel10.setText("Grade : C");
        jLabel11.setText("Hasil : Lulus");
    }else{
            jLabel10.setText("Grade : D");
            jLabel11.setText("Hasil : Tidak Lulus");
            }
    }//GEN-LAST:event_jButton1ActionPerformed

